import React from "react";
import "./ExpenseItem.css";
import ExpenseDate from "./ExpenseDate";

interface Props {
  title: string;
  amount: number;
  date: object;
}

export default function ExpenseItem({ title, amount, date }: Props) {
  // @ts-ignore
  const month: string = date.toLocaleString("en-us", { month: "long" });
  // @ts-ignore
  const year: string = date.toLocaleString("en-us", { day: "2-digit" });
  // @ts-ignore
  const day: string = date.getFullYear();

  return (
    <div className="expense-item">
      <ExpenseDate />
      <div className="expense-item__description">
        <h2>{title}</h2>
        <div className="expense-item__price">{amount}</div>
      </div>
    </div>
  );
}
